INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0001-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-001', 'Ransomware Attack', 'Threat of ransomware encrypting critical business data and systems, demanding payment for decryption keys. Includes risk of data exfiltration before encryption (double extortion).', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['ransomware', 'malware', 'data-loss', 'critical']),
('rt000000-0000-0000-0001-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-002', 'Phishing / Social Engineering', 'Risk of employees falling victim to phishing emails, spear-phishing, or social engineering attacks leading to credential theft or malware installation.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['phishing', 'social-engineering', 'credential-theft']),
('rt000000-0000-0000-0001-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-003', 'Advanced Persistent Threat (APT)', 'Risk of sophisticated, targeted attack by a well-resourced adversary gaining long-term access to systems and data.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['apt', 'targeted-attack', 'nation-state']),
('rt000000-0000-0000-0001-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-004', 'SQL Injection / Web Application Attack', 'Risk of attackers exploiting web application vulnerabilities (SQLi, XSS, SSRF) to access or modify data.', 'cyber_security', 'identified', TRUE, 'OWASP Top 10', ARRAY['web-app', 'injection', 'owasp']),
('rt000000-0000-0000-0001-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-005', 'Credential Stuffing / Brute Force', 'Risk of automated attacks using stolen credentials or brute-force methods to gain unauthorized access.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['credential-stuffing', 'brute-force', 'authentication']),
('rt000000-0000-0000-0001-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-006', 'Zero-Day Vulnerability Exploitation', 'Risk of attackers exploiting previously unknown vulnerabilities before patches are available.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['zero-day', 'vulnerability', 'exploit']),
('rt000000-0000-0000-0001-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-007', 'DDoS Attack', 'Risk of distributed denial-of-service attacks rendering critical services unavailable to legitimate users.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['ddos', 'availability', 'dos']),
('rt000000-0000-0000-0001-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-008', 'Supply Chain Compromise', 'Risk of a trusted software vendor or dependency being compromised, introducing malicious code into the environment.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['supply-chain', 'vendor', 'software-compromise']),
('rt000000-0000-0000-0001-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-009', 'Man-in-the-Middle Attack', 'Risk of intercepted communications allowing eavesdropping or data manipulation between parties.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['mitm', 'encryption', 'interception']),
('rt000000-0000-0000-0001-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-010', 'Insider Threat — Malicious', 'Risk of a trusted insider deliberately stealing data, sabotaging systems, or selling access.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['insider-threat', 'malicious', 'data-theft']),
('rt000000-0000-0000-0001-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-011', 'Cryptographic Key Compromise', 'Risk of encryption keys being stolen, leaked, or improperly managed, rendering encrypted data accessible.', 'cyber_security', 'identified', TRUE, 'ISO 27005', ARRAY['cryptography', 'key-management', 'encryption']),
('rt000000-0000-0000-0001-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-012', 'DNS Hijacking / Poisoning', 'Risk of DNS infrastructure being compromised, redirecting traffic to malicious servers.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['dns', 'hijacking', 'network']),
('rt000000-0000-0000-0001-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-013', 'API Security Breach', 'Risk of API vulnerabilities being exploited including broken authentication, excessive data exposure, and rate limiting failures.', 'cyber_security', 'identified', TRUE, 'OWASP API Top 10', ARRAY['api', 'authentication', 'data-exposure']),
('rt000000-0000-0000-0001-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-014', 'Unpatched Systems / Missing Updates', 'Risk of known vulnerabilities remaining exploitable due to delayed or missed patching.', 'cyber_security', 'identified', TRUE, 'CIS Controls', ARRAY['patching', 'vulnerability', 'maintenance']),
('rt000000-0000-0000-0001-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-015', 'Cloud Misconfiguration', 'Risk of cloud resources being misconfigured (open S3 buckets, overly permissive IAM, public databases), exposing data or services.', 'cyber_security', 'identified', TRUE, 'CIS Controls', ARRAY['cloud', 'misconfiguration', 'aws', 'azure', 'gcp']),
('rt000000-0000-0000-0001-000000000016', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-016', 'Watering Hole Attack', 'Risk of legitimate websites frequently visited by employees being compromised to deliver targeted malware.', 'cyber_security', 'identified', TRUE, 'MITRE ATT&CK', ARRAY['watering-hole', 'drive-by', 'targeted']),
('rt000000-0000-0000-0001-000000000017', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-017', 'Malware / Trojan Infection', 'Risk of systems being infected by malware through email attachments, downloads, or removable media.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['malware', 'trojan', 'infection']),
('rt000000-0000-0000-0001-000000000018', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-018', 'Wireless Network Exploitation', 'Risk of unauthorized access through rogue access points, evil twin attacks, or WPA/WPA2 cracking.', 'cyber_security', 'identified', TRUE, 'CIS Controls', ARRAY['wireless', 'wifi', 'rogue-ap']),
('rt000000-0000-0000-0001-000000000019', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-019', 'Session Hijacking', 'Risk of authenticated sessions being stolen via cookie theft, cross-site scripting, or session fixation.', 'cyber_security', 'identified', TRUE, 'OWASP Top 10', ARRAY['session', 'cookie', 'hijacking']),
('rt000000-0000-0000-0001-000000000020', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-020', 'Privilege Escalation', 'Risk of attackers elevating their access from standard user to administrator through misconfigurations or exploits.', 'cyber_security', 'identified', TRUE, 'MITRE ATT&CK', ARRAY['privilege-escalation', 'lateral-movement', 'access']),
('rt000000-0000-0000-0001-000000000021', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-021', 'Data Exfiltration via Covert Channel', 'Risk of data being exfiltrated through DNS tunneling, steganography, or other covert communication channels.', 'cyber_security', 'identified', TRUE, 'MITRE ATT&CK', ARRAY['exfiltration', 'covert-channel', 'dns-tunnel']),
('rt000000-0000-0000-0001-000000000022', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-022', 'Fileless Malware Attack', 'Risk of memory-resident malware evading traditional antivirus by operating entirely in RAM without touching disk.', 'cyber_security', 'identified', TRUE, 'MITRE ATT&CK', ARRAY['fileless', 'memory-resident', 'evasion']),
('rt000000-0000-0000-0001-000000000023', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-023', 'Business Email Compromise (BEC)', 'Risk of impersonation of executives or vendors via compromised or spoofed email accounts to initiate fraudulent transactions.', 'cyber_security', 'identified', TRUE, 'FBI IC3', ARRAY['bec', 'impersonation', 'fraud']),
('rt000000-0000-0000-0001-000000000024', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-024', 'IoT Device Compromise', 'Risk of insecure IoT devices providing entry points for attackers into the corporate network.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-183', ARRAY['iot', 'device', 'network-entry']),
('rt000000-0000-0000-0001-000000000025', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-025', 'Cross-Site Request Forgery (CSRF)', 'Risk of authenticated users being tricked into making unintended requests to web applications.', 'cyber_security', 'identified', TRUE, 'OWASP Top 10', ARRAY['csrf', 'web-app', 'forgery']),
('rt000000-0000-0000-0001-000000000026', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-026', 'Code Injection via Dependency', 'Risk of malicious code introduced via compromised or typosquatted open-source packages in build pipelines.', 'cyber_security', 'identified', TRUE, 'SLSA Framework', ARRAY['dependency', 'typosquatting', 'sca']),
('rt000000-0000-0000-0001-000000000027', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-027', 'Mobile Device Compromise', 'Risk of corporate data being exposed through compromised, lost, or stolen mobile devices.', 'cyber_security', 'identified', TRUE, 'CIS Controls', ARRAY['mobile', 'byod', 'device-theft']),
('rt000000-0000-0000-0001-000000000028', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-028', 'Side-Channel Attack', 'Risk of sensitive data being extracted via timing, power, or electromagnetic side-channel analysis.', 'cyber_security', 'identified', TRUE, 'NIST SP 800-30', ARRAY['side-channel', 'timing', 'hardware']),
('rt000000-0000-0000-0001-000000000029', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-029', 'Deepfake / AI-Generated Social Engineering', 'Risk of AI-generated voice or video deepfakes being used to impersonate executives for social engineering attacks.', 'cyber_security', 'identified', TRUE, 'General', ARRAY['deepfake', 'ai', 'impersonation', 'social-engineering']),
('rt000000-0000-0000-0001-000000000030', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CY-030', 'Quantum Computing Threat', 'Long-term risk of quantum computers breaking current asymmetric encryption (RSA, ECC), compromising encrypted data.', 'cyber_security', 'identified', TRUE, 'NIST PQC', ARRAY['quantum', 'encryption', 'long-term', 'post-quantum'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0002-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-001', 'System / Service Outage', 'Risk of critical systems becoming unavailable due to hardware failure, software bugs, or infrastructure issues.', 'operational', 'identified', TRUE, 'ISO 27005', ARRAY['outage', 'availability', 'downtime']),
('rt000000-0000-0000-0002-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-002', 'Data Loss / Corruption', 'Risk of data being lost, corrupted, or rendered irrecoverable due to hardware failure, software bugs, or human error.', 'operational', 'identified', TRUE, 'ISO 27005', ARRAY['data-loss', 'corruption', 'backup']),
('rt000000-0000-0000-0002-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-003', 'Backup Failure', 'Risk of backup systems failing silently, leading to inability to recover data after an incident.', 'operational', 'identified', TRUE, 'ISO 27005', ARRAY['backup', 'recovery', 'data-loss']),
('rt000000-0000-0000-0002-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-004', 'Configuration Drift', 'Risk of production systems drifting from approved configurations, introducing security gaps or instability.', 'operational', 'identified', TRUE, 'CIS Controls', ARRAY['configuration', 'drift', 'compliance']),
('rt000000-0000-0000-0002-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-005', 'Change Management Failure', 'Risk of poorly managed changes causing outages, security vulnerabilities, or data loss.', 'operational', 'identified', TRUE, 'ITIL', ARRAY['change-management', 'deployment', 'process']),
('rt000000-0000-0000-0002-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-006', 'Capacity Exhaustion', 'Risk of storage, compute, or network capacity being exhausted, causing service degradation or outage.', 'operational', 'identified', TRUE, 'ISO 27005', ARRAY['capacity', 'scaling', 'performance']),
('rt000000-0000-0000-0002-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-007', 'Monitoring / Alerting Gap', 'Risk of security incidents or system failures going undetected due to insufficient monitoring or alerting.', 'operational', 'identified', TRUE, 'CIS Controls', ARRAY['monitoring', 'alerting', 'detection']),
('rt000000-0000-0000-0002-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-008', 'Incident Response Inadequacy', 'Risk of the organization being unable to effectively detect, respond to, contain, and recover from security incidents.', 'operational', 'identified', TRUE, 'NIST CSF', ARRAY['incident-response', 'readiness', 'recovery']),
('rt000000-0000-0000-0002-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-009', 'Disaster Recovery Failure', 'Risk of disaster recovery plans being inadequate, untested, or failing when needed.', 'operational', 'identified', TRUE, 'ISO 22301', ARRAY['disaster-recovery', 'bcp', 'resilience']),
('rt000000-0000-0000-0002-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-010', 'Shadow IT', 'Risk of unauthorized applications, services, or devices being used by employees outside IT governance.', 'operational', 'identified', TRUE, 'CIS Controls', ARRAY['shadow-it', 'unauthorized', 'governance']),
('rt000000-0000-0000-0002-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-011', 'Deployment Pipeline Breakage', 'Risk of CI/CD pipeline failures causing delayed releases, broken builds, or unintended production deployments.', 'operational', 'identified', TRUE, 'DORA', ARRAY['ci-cd', 'deployment', 'pipeline']),
('rt000000-0000-0000-0002-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-012', 'Database Performance Degradation', 'Risk of database performance deteriorating due to query bloat, missing indexes, or resource contention.', 'operational', 'identified', TRUE, 'General', ARRAY['database', 'performance', 'degradation']),
('rt000000-0000-0000-0002-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-013', 'DNS / Network Resolution Failure', 'Risk of DNS or name resolution services becoming unavailable, causing widespread service disruption.', 'operational', 'identified', TRUE, 'ISO 27005', ARRAY['dns', 'network', 'resolution']),
('rt000000-0000-0000-0002-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-014', 'Log Aggregation Failure', 'Risk of centralized logging systems failing, creating gaps in audit trails and delaying incident detection.', 'operational', 'identified', TRUE, 'CIS Controls', ARRAY['logging', 'siem', 'aggregation']),
('rt000000-0000-0000-0002-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-015', 'Certificate Expiration', 'Risk of TLS/SSL certificates expiring unnoticed, causing service outages and security warnings.', 'operational', 'identified', TRUE, 'General', ARRAY['certificate', 'tls', 'expiration']),
('rt000000-0000-0000-0002-000000000016', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-016', 'API Rate Limiting Failure', 'Risk of API rate limiting being misconfigured or absent, allowing abuse and resource exhaustion.', 'operational', 'identified', TRUE, 'OWASP API', ARRAY['api', 'rate-limiting', 'abuse']),
('rt000000-0000-0000-0002-000000000017', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-017', 'Runbook / Procedure Gap', 'Risk of missing or outdated operational runbooks leading to inconsistent incident handling and extended outages.', 'operational', 'identified', TRUE, 'ITIL', ARRAY['runbook', 'documentation', 'procedures']),
('rt000000-0000-0000-0002-000000000018', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-018', 'Multi-Region Failover Failure', 'Risk of cross-region failover mechanisms not working as designed during a regional outage.', 'operational', 'identified', TRUE, 'ISO 22301', ARRAY['failover', 'multi-region', 'resilience']),
('rt000000-0000-0000-0002-000000000019', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-019', 'Secrets Rotation Failure', 'Risk of automated secrets/credential rotation failing, leaving stale credentials active beyond policy limits.', 'operational', 'identified', TRUE, 'CIS Controls', ARRAY['secrets', 'rotation', 'credentials']),
('rt000000-0000-0000-0002-000000000020', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-020', 'Third-Party API Dependency Outage', 'Risk of critical third-party APIs (payment, auth, messaging) becoming unavailable and disrupting business operations.', 'operational', 'identified', TRUE, 'General', ARRAY['third-party', 'api', 'dependency']),
('rt000000-0000-0000-0002-000000000021', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-021', 'Queue / Message Broker Failure', 'Risk of message brokers (Kafka, RabbitMQ, SQS) failing, causing data loss or processing delays.', 'operational', 'identified', TRUE, 'General', ARRAY['queue', 'message-broker', 'processing']),
('rt000000-0000-0000-0002-000000000022', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-022', 'Container Orchestration Failure', 'Risk of Kubernetes or container orchestration platform failures causing pod evictions, scheduling issues, or cluster instability.', 'operational', 'identified', TRUE, 'CIS Kubernetes', ARRAY['kubernetes', 'container', 'orchestration']),
('rt000000-0000-0000-0002-000000000023', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-023', 'Data Migration Error', 'Risk of data migration or transformation errors causing data loss, corruption, or inconsistency.', 'operational', 'identified', TRUE, 'General', ARRAY['migration', 'data-loss', 'transformation']),
('rt000000-0000-0000-0002-000000000024', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-024', 'Cron Job / Scheduled Task Failure', 'Risk of scheduled jobs failing silently, causing data staleness, missed reports, or compliance gaps.', 'operational', 'identified', TRUE, 'General', ARRAY['cron', 'scheduled-task', 'automation']),
('rt000000-0000-0000-0002-000000000025', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-OP-025', 'Insufficient Capacity Planning', 'Risk of inadequate capacity planning leading to performance degradation during peak usage or growth periods.', 'operational', 'identified', TRUE, 'ITIL', ARRAY['capacity', 'planning', 'growth'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0003-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-001', 'PCI DSS Non-Compliance', 'Risk of failing PCI DSS audit resulting in fines, increased transaction fees, or loss of card processing ability.', 'compliance', 'identified', TRUE, 'PCI DSS v4.0.1', ARRAY['pci-dss', 'payment', 'audit']),
('rt000000-0000-0000-0003-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-002', 'SOC 2 Audit Failure', 'Risk of receiving a qualified SOC 2 opinion due to control deficiencies, impacting customer trust and sales.', 'compliance', 'identified', TRUE, 'AICPA TSC', ARRAY['soc2', 'audit', 'trust']),
('rt000000-0000-0000-0003-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-003', 'GDPR Violation', 'Risk of violating GDPR requirements resulting in fines up to 4% of global annual turnover or €20M.', 'compliance', 'identified', TRUE, 'GDPR', ARRAY['gdpr', 'privacy', 'european-union', 'fine']),
('rt000000-0000-0000-0003-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-004', 'CCPA/CPRA Violation', 'Risk of violating California privacy regulations resulting in fines and class action lawsuits.', 'compliance', 'identified', TRUE, 'CCPA/CPRA', ARRAY['ccpa', 'cpra', 'privacy', 'california']),
('rt000000-0000-0000-0003-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-005', 'ISO 27001 Certification Loss', 'Risk of losing ISO 27001 certification due to non-conformities found during surveillance or recertification audits.', 'compliance', 'identified', TRUE, 'ISO 27001', ARRAY['iso27001', 'certification', 'audit']),
('rt000000-0000-0000-0003-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-006', 'Regulatory Change Impact', 'Risk of new or amended regulations requiring significant changes to policies, controls, or business processes.', 'compliance', 'identified', TRUE, 'General', ARRAY['regulation', 'change', 'adaptation']),
('rt000000-0000-0000-0003-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-007', 'Evidence Staleness', 'Risk of compliance evidence becoming stale, outdated, or insufficient before audit deadlines.', 'compliance', 'identified', TRUE, 'General', ARRAY['evidence', 'staleness', 'audit-readiness']),
('rt000000-0000-0000-0003-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-008', 'Audit Trail Tampering', 'Risk of audit logs being modified, deleted, or rendered unreliable, undermining compliance evidence integrity.', 'compliance', 'identified', TRUE, 'ISO 27001', ARRAY['audit-log', 'integrity', 'tampering']),
('rt000000-0000-0000-0003-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-009', 'Cross-Border Data Transfer Violation', 'Risk of transferring personal data across borders in violation of data residency or transfer mechanism requirements.', 'compliance', 'identified', TRUE, 'GDPR', ARRAY['data-transfer', 'cross-border', 'schrems']),
('rt000000-0000-0000-0003-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-010', 'Inadequate Privacy Notice', 'Risk of privacy notices being incomplete, inaccurate, or not properly displayed, violating transparency requirements.', 'compliance', 'identified', TRUE, 'GDPR', ARRAY['privacy-notice', 'transparency', 'consent']),
('rt000000-0000-0000-0003-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-011', 'SOX IT Controls Deficiency', 'Risk of IT general controls deficiencies impacting financial reporting integrity under Sarbanes-Oxley requirements.', 'compliance', 'identified', TRUE, 'SOX', ARRAY['sox', 'itgc', 'financial-reporting']),
('rt000000-0000-0000-0003-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-012', 'HIPAA Security Rule Violation', 'Risk of failing to implement required administrative, physical, and technical safeguards for protected health information.', 'compliance', 'identified', TRUE, 'HIPAA', ARRAY['hipaa', 'phi', 'healthcare']),
('rt000000-0000-0000-0003-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-013', 'FedRAMP Authorization Gap', 'Risk of failing to meet FedRAMP requirements for government cloud services, losing authorization to operate.', 'compliance', 'identified', TRUE, 'FedRAMP', ARRAY['fedramp', 'government', 'ato']),
('rt000000-0000-0000-0003-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-014', 'Export Control Violation', 'Risk of violating export control regulations (EAR, ITAR) through improper technology or data transfer.', 'compliance', 'identified', TRUE, 'EAR/ITAR', ARRAY['export-control', 'itar', 'ear']),
('rt000000-0000-0000-0003-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-015', 'Industry-Specific Regulation Non-Compliance', 'Risk of failing to comply with industry-specific regulations (FINRA, SEC, FDA, etc.) applicable to the business.', 'compliance', 'identified', TRUE, 'General', ARRAY['industry-regulation', 'sector-specific', 'compliance']),
('rt000000-0000-0000-0003-000000000016', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-016', 'Accessibility Non-Compliance (ADA/WCAG)', 'Risk of digital services failing to meet accessibility requirements under ADA or WCAG 2.1 standards.', 'compliance', 'identified', TRUE, 'WCAG 2.1', ARRAY['accessibility', 'ada', 'wcag']),
('rt000000-0000-0000-0003-000000000017', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-017', 'Reporting Deadline Failure', 'Risk of missing mandatory regulatory reporting deadlines for breach notifications, filings, or attestations.', 'compliance', 'identified', TRUE, 'General', ARRAY['reporting', 'deadline', 'notification']),
('rt000000-0000-0000-0003-000000000018', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-018', 'Policy Documentation Gap', 'Risk of required policies being missing, outdated, or not properly approved, failing audit requirements.', 'compliance', 'identified', TRUE, 'ISO 27001', ARRAY['policy', 'documentation', 'governance']),
('rt000000-0000-0000-0003-000000000019', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-019', 'Vendor Compliance Attestation Gap', 'Risk of vendors failing to provide required compliance attestations (SOC reports, pentest results) in a timely manner.', 'compliance', 'identified', TRUE, 'General', ARRAY['vendor', 'attestation', 'third-party']),
('rt000000-0000-0000-0003-000000000020', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-020', 'Employee Compliance Training Deficiency', 'Risk of employees not completing required compliance training (security awareness, privacy, code of conduct).', 'compliance', 'identified', TRUE, 'General', ARRAY['training', 'compliance', 'awareness']),
('rt000000-0000-0000-0003-000000000021', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-021', 'Data Localization Requirement Breach', 'Risk of data stored or processed outside jurisdictions required by local data localization laws.', 'compliance', 'identified', TRUE, 'General', ARRAY['data-localization', 'sovereignty', 'residency']),
('rt000000-0000-0000-0003-000000000022', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-022', 'Penetration Testing Gap', 'Risk of not conducting required penetration tests at mandated intervals, failing compliance requirements.', 'compliance', 'identified', TRUE, 'PCI DSS', ARRAY['pentest', 'testing', 'mandate']),
('rt000000-0000-0000-0003-000000000023', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-023', 'Business Continuity Plan Non-Compliance', 'Risk of BCP/DRP not meeting regulatory requirements for testing frequency, scope, or documentation.', 'compliance', 'identified', TRUE, 'ISO 22301', ARRAY['bcp', 'drp', 'testing']),
('rt000000-0000-0000-0003-000000000024', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-024', 'Cloud Shared Responsibility Misunderstanding', 'Risk of misunderstanding the shared responsibility model leading to unaddressed compliance obligations in cloud environments.', 'compliance', 'identified', TRUE, 'CSA CCM', ARRAY['cloud', 'shared-responsibility', 'iaas', 'paas']),
('rt000000-0000-0000-0003-000000000025', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-CO-025', 'AI Regulation Non-Compliance', 'Risk of AI/ML systems failing to comply with emerging AI regulations (EU AI Act, NIST AI RMF) regarding transparency and fairness.', 'compliance', 'identified', TRUE, 'EU AI Act', ARRAY['ai', 'regulation', 'fairness', 'transparency'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0004-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-001', 'Personal Data Breach', 'Risk of unauthorized access to, or disclosure of, personal data affecting data subjects.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 4(12)', ARRAY['data-breach', 'pii', 'notification']),
('rt000000-0000-0000-0004-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-002', 'Excessive Data Collection', 'Risk of collecting more personal data than necessary for the stated purpose, violating data minimization principles.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 5(1)(c)', ARRAY['data-minimization', 'collection', 'purpose-limitation']),
('rt000000-0000-0000-0004-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-003', 'Consent Management Failure', 'Risk of failing to properly obtain, record, or manage data subject consent for processing activities.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 7', ARRAY['consent', 'lawful-basis', 'opt-in']),
('rt000000-0000-0000-0004-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-004', 'Data Subject Rights Non-Fulfillment', 'Risk of failing to fulfill data subject access, deletion, portability, or rectification requests within required timeframes.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 15-22', ARRAY['dsar', 'subject-rights', 'access-request']),
('rt000000-0000-0000-0004-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-005', 'Data Retention Violation', 'Risk of retaining personal data longer than necessary or failing to securely dispose of expired data.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 5(1)(e)', ARRAY['retention', 'disposal', 'storage-limitation']),
('rt000000-0000-0000-0004-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-006', 'Unauthorized Data Sharing', 'Risk of personal data being shared with unauthorized third parties without proper agreements or consent.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 28', ARRAY['data-sharing', 'third-party', 'dpa']),
('rt000000-0000-0000-0004-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-007', 'Inadequate Data Protection Impact Assessment', 'Risk of processing activities requiring DPIA being conducted without adequate risk assessment.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 35', ARRAY['dpia', 'impact-assessment', 'high-risk']),
('rt000000-0000-0000-0004-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-008', 'Breach Notification Failure', 'Risk of failing to notify supervisory authorities and affected individuals within required timeframes after a data breach.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 33-34', ARRAY['breach-notification', '72-hour', 'supervisory-authority']),
('rt000000-0000-0000-0004-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-009', 'Children Data Processing Violation', 'Risk of processing children''s personal data without appropriate parental consent and safeguards.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 8', ARRAY['children', 'parental-consent', 'minors']),
('rt000000-0000-0000-0004-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-010', 'Automated Decision-Making Bias', 'Risk of automated decision-making or profiling systems producing biased or discriminatory outcomes.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 22', ARRAY['automated-decision', 'profiling', 'bias', 'ai']),
('rt000000-0000-0000-0004-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-011', 'Data Processing Agreement Gap', 'Risk of data processors operating without valid DPAs, leaving responsibilities and liabilities undefined.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 28', ARRAY['dpa', 'processor', 'contract']),
('rt000000-0000-0000-0004-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-012', 'Employee Privacy Violation', 'Risk of employee personal data (health, performance, compensation) being accessed or disclosed without authorization.', 'data_privacy', 'identified', TRUE, 'General', ARRAY['employee-privacy', 'hr-data', 'confidentiality']),
('rt000000-0000-0000-0004-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-013', 'Analytics / Tracking Overcollection', 'Risk of website analytics, cookies, or tracking pixels collecting personal data beyond what users consented to.', 'data_privacy', 'identified', TRUE, 'ePrivacy', ARRAY['analytics', 'tracking', 'cookies', 'consent']),
('rt000000-0000-0000-0004-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-014', 'Pseudonymization / Anonymization Failure', 'Risk of pseudonymized or anonymized data being re-identifiable through linkage attacks or insufficient techniques.', 'data_privacy', 'identified', TRUE, 'GDPR Recital 26', ARRAY['pseudonymization', 'anonymization', 're-identification']),
('rt000000-0000-0000-0004-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-015', 'Privacy by Design Non-Implementation', 'Risk of new products and features being developed without incorporating privacy-by-design and privacy-by-default principles.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 25', ARRAY['privacy-by-design', 'development', 'sdlc']),
('rt000000-0000-0000-0004-000000000016', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-016', 'Records of Processing Gap', 'Risk of failing to maintain complete and accurate records of processing activities as required by regulation.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 30', ARRAY['ropa', 'processing-records', 'documentation']),
('rt000000-0000-0000-0004-000000000017', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-017', 'Marketing Communication Violation', 'Risk of sending marketing communications without proper consent or failing to honor opt-out requests.', 'data_privacy', 'identified', TRUE, 'CAN-SPAM/CASL', ARRAY['marketing', 'opt-out', 'spam']),
('rt000000-0000-0000-0004-000000000018', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-018', 'Data Portability Non-Compliance', 'Risk of being unable to provide data subjects with their personal data in a structured, machine-readable format.', 'data_privacy', 'identified', TRUE, 'GDPR Art. 20', ARRAY['portability', 'export', 'machine-readable']),
('rt000000-0000-0000-0004-000000000019', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-019', 'Biometric Data Mishandling', 'Risk of biometric data (fingerprints, facial recognition) being collected, stored, or processed without proper safeguards and consent.', 'data_privacy', 'identified', TRUE, 'BIPA/GDPR', ARRAY['biometric', 'fingerprint', 'facial-recognition']),
('rt000000-0000-0000-0004-000000000020', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-DP-020', 'Third-Party Data Enrichment Risk', 'Risk of enriching datasets with third-party data that was not lawfully collected or for which consent does not cover the enrichment purpose.', 'data_privacy', 'identified', TRUE, 'General', ARRAY['data-enrichment', 'third-party', 'lawful-basis'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0005-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-001', 'Legacy System Dependency', 'Risk of critical business processes depending on outdated, unsupported, or end-of-life systems.', 'technology', 'identified', TRUE, 'ISO 27005', ARRAY['legacy', 'eol', 'technical-debt']),
('rt000000-0000-0000-0005-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-002', 'Single Point of Failure', 'Risk of a single component failure causing cascading outage across dependent systems.', 'technology', 'identified', TRUE, 'ISO 27005', ARRAY['spof', 'redundancy', 'resilience']),
('rt000000-0000-0000-0005-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-003', 'Database Compromise', 'Risk of database being compromised through SQL injection, misconfiguration, or stolen credentials.', 'technology', 'identified', TRUE, 'OWASP Top 10', ARRAY['database', 'sql-injection', 'data-access']),
('rt000000-0000-0000-0005-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-004', 'Container / Kubernetes Escape', 'Risk of container isolation being bypassed, allowing access to host systems or other containers.', 'technology', 'identified', TRUE, 'CIS Kubernetes Benchmark', ARRAY['container', 'kubernetes', 'escape', 'isolation']),
('rt000000-0000-0000-0005-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-005', 'CI/CD Pipeline Compromise', 'Risk of build/deployment pipelines being compromised, enabling injection of malicious code into production.', 'technology', 'identified', TRUE, 'SLSA Framework', ARRAY['ci-cd', 'pipeline', 'build', 'deployment']),
('rt000000-0000-0000-0005-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-006', 'Secrets / Credential Exposure', 'Risk of API keys, passwords, tokens, or certificates being exposed in code repositories, logs, or configuration files.', 'technology', 'identified', TRUE, 'CIS Controls', ARRAY['secrets', 'credentials', 'exposure', 'hardcoded']),
('rt000000-0000-0000-0005-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-007', 'Third-Party Dependency Vulnerability', 'Risk of vulnerabilities in open-source libraries or third-party packages used in applications.', 'technology', 'identified', TRUE, 'OWASP', ARRAY['dependency', 'sca', 'open-source', 'vulnerability']),
('rt000000-0000-0000-0005-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-008', 'TLS / Certificate Misconfiguration', 'Risk of TLS certificates expiring, being misconfigured, or using weak cipher suites.', 'technology', 'identified', TRUE, 'NIST SP 800-52', ARRAY['tls', 'certificate', 'encryption', 'https']),
('rt000000-0000-0000-0005-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-009', 'Cloud Service Provider Outage', 'Risk of a major cloud provider experiencing an outage affecting business operations.', 'technology', 'identified', TRUE, 'ISO 27005', ARRAY['cloud', 'outage', 'vendor-dependency']),
('rt000000-0000-0000-0005-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-010', 'Insufficient Logging / Audit Trail', 'Risk of security events not being logged or logs being insufficiently detailed for forensic investigation.', 'technology', 'identified', TRUE, 'CIS Controls', ARRAY['logging', 'audit-trail', 'forensics']),
('rt000000-0000-0000-0005-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-011', 'Infrastructure as Code Misconfiguration', 'Risk of IaC templates (Terraform, CloudFormation) containing security misconfigurations propagated across environments.', 'technology', 'identified', TRUE, 'CIS Controls', ARRAY['iac', 'terraform', 'cloudformation', 'misconfiguration']),
('rt000000-0000-0000-0005-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-012', 'Shadow API / Undocumented Endpoint', 'Risk of undocumented or forgotten APIs remaining accessible and unmonitored in production.', 'technology', 'identified', TRUE, 'OWASP API', ARRAY['shadow-api', 'undocumented', 'endpoint']),
('rt000000-0000-0000-0005-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-013', 'Microservice Communication Insecurity', 'Risk of inter-service communication lacking mutual TLS, authentication, or authorization in microservice architectures.', 'technology', 'identified', TRUE, 'NIST SP 800-204', ARRAY['microservice', 'mtls', 'service-mesh']),
('rt000000-0000-0000-0005-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-014', 'Object Storage Data Exposure', 'Risk of cloud object storage (S3, GCS, Blob) being publicly accessible or insufficiently access-controlled.', 'technology', 'identified', TRUE, 'CIS Controls', ARRAY['s3', 'object-storage', 'public-access']),
('rt000000-0000-0000-0005-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-015', 'Serverless Function Vulnerability', 'Risk of serverless functions (Lambda, Cloud Functions) having excessive permissions, injection flaws, or dependency vulnerabilities.', 'technology', 'identified', TRUE, 'OWASP Serverless', ARRAY['serverless', 'lambda', 'function', 'permissions']),
('rt000000-0000-0000-0005-000000000016', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-016', 'Database Replication Lag', 'Risk of database replication lag causing data inconsistency between primary and replica nodes.', 'technology', 'identified', TRUE, 'General', ARRAY['replication', 'consistency', 'database']),
('rt000000-0000-0000-0005-000000000017', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-017', 'Cache Poisoning', 'Risk of web or CDN caches being poisoned, serving malicious or stale content to users.', 'technology', 'identified', TRUE, 'OWASP', ARRAY['cache', 'cdn', 'poisoning']),
('rt000000-0000-0000-0005-000000000018', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-018', 'Email Security Misconfiguration', 'Risk of email security records (SPF, DKIM, DMARC) being misconfigured, enabling spoofing and phishing impersonation.', 'technology', 'identified', TRUE, 'NIST SP 800-177', ARRAY['email', 'spf', 'dkim', 'dmarc']),
('rt000000-0000-0000-0005-000000000019', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-019', 'Build Artifact Tampering', 'Risk of build artifacts being modified between build and deployment, introducing unauthorized changes.', 'technology', 'identified', TRUE, 'SLSA Framework', ARRAY['build', 'artifact', 'integrity', 'signing']),
('rt000000-0000-0000-0005-000000000020', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-020', 'Deprecated Protocol Usage', 'Risk of systems using deprecated protocols (TLS 1.0/1.1, SSLv3, FTP, Telnet) vulnerable to known attacks.', 'technology', 'identified', TRUE, 'NIST SP 800-52', ARRAY['deprecated', 'protocol', 'tls', 'legacy']),
('rt000000-0000-0000-0005-000000000021', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-021', 'GraphQL Introspection Exposure', 'Risk of GraphQL introspection being enabled in production, revealing the entire API schema to attackers.', 'technology', 'identified', TRUE, 'OWASP API', ARRAY['graphql', 'introspection', 'schema-exposure']),
('rt000000-0000-0000-0005-000000000022', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-022', 'Insecure Deserialization', 'Risk of deserialization of untrusted data leading to remote code execution or injection attacks.', 'technology', 'identified', TRUE, 'OWASP Top 10', ARRAY['deserialization', 'rce', 'injection']),
('rt000000-0000-0000-0005-000000000023', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-023', 'AI/ML Model Poisoning', 'Risk of AI/ML models being manipulated through adversarial training data, backdoors, or prompt injection.', 'technology', 'identified', TRUE, 'NIST AI RMF', ARRAY['ai', 'ml', 'poisoning', 'adversarial']),
('rt000000-0000-0000-0005-000000000024', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-024', 'Observability Stack Compromise', 'Risk of monitoring/observability tools (Datadog, Grafana, Splunk) being compromised, exposing sensitive operational data.', 'technology', 'identified', TRUE, 'General', ARRAY['observability', 'monitoring', 'compromise']),
('rt000000-0000-0000-0005-000000000025', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TE-025', 'Technical Debt Accumulation', 'Risk of accumulating technical debt reducing system maintainability, increasing vulnerability surface, and slowing delivery.', 'technology', 'identified', TRUE, 'General', ARRAY['technical-debt', 'maintainability', 'velocity'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0006-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-001', 'Vendor Data Breach', 'Risk of a third-party vendor experiencing a data breach that exposes organizational data.', 'third_party', 'identified', TRUE, 'ISO 27036', ARRAY['vendor', 'data-breach', 'third-party']),
('rt000000-0000-0000-0006-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-002', 'Vendor Service Disruption', 'Risk of a critical vendor service becoming unavailable, disrupting business operations.', 'third_party', 'identified', TRUE, 'ISO 27036', ARRAY['vendor', 'disruption', 'sla']),
('rt000000-0000-0000-0006-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-003', 'Vendor Lock-In', 'Risk of becoming overly dependent on a single vendor, limiting flexibility and negotiating power.', 'third_party', 'identified', TRUE, 'General', ARRAY['vendor-lock-in', 'dependency', 'migration']),
('rt000000-0000-0000-0006-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-004', 'Inadequate Vendor Security Controls', 'Risk of vendors not maintaining adequate security controls, creating exposure for shared data and systems.', 'third_party', 'identified', TRUE, 'SIG/CAIQ', ARRAY['vendor-security', 'assessment', 'controls']),
('rt000000-0000-0000-0006-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-005', 'Fourth-Party Risk', 'Risk of vendors subcontracting to additional parties without adequate oversight.', 'third_party', 'identified', TRUE, 'ISO 27036', ARRAY['fourth-party', 'subcontractor', 'supply-chain']),
('rt000000-0000-0000-0006-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-006', 'Vendor Compliance Failure', 'Risk of vendors failing to maintain required compliance certifications (SOC 2, PCI DSS, ISO 27001).', 'third_party', 'identified', TRUE, 'General', ARRAY['vendor-compliance', 'certification', 'audit']),
('rt000000-0000-0000-0006-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-007', 'Vendor Financial Instability', 'Risk of a critical vendor becoming financially unstable or ceasing operations.', 'third_party', 'identified', TRUE, 'General', ARRAY['vendor-financial', 'bankruptcy', 'continuity']),
('rt000000-0000-0000-0006-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-008', 'Data Processing Agreement Non-Compliance', 'Risk of vendor data processing activities not conforming to DPA terms and conditions.', 'third_party', 'identified', TRUE, 'GDPR Art. 28', ARRAY['dpa', 'processing', 'vendor-compliance']),
('rt000000-0000-0000-0006-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-009', 'API Integration Security Gaps', 'Risk of insecure API integrations with vendors creating authentication, authorization, or data exposure vulnerabilities.', 'third_party', 'identified', TRUE, 'OWASP API', ARRAY['api', 'integration', 'vendor-api']),
('rt000000-0000-0000-0006-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-010', 'Vendor Access Overprovisioning', 'Risk of vendors retaining excessive access privileges to systems and data beyond what is needed.', 'third_party', 'identified', TRUE, 'CIS Controls', ARRAY['vendor-access', 'least-privilege', 'access-review']),
('rt000000-0000-0000-0006-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-011', 'SaaS Shadow Data', 'Risk of organizational data accumulating in SaaS vendor environments without adequate visibility or control.', 'third_party', 'identified', TRUE, 'General', ARRAY['saas', 'shadow-data', 'visibility']),
('rt000000-0000-0000-0006-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-012', 'Vendor Personnel Change Risk', 'Risk of key vendor personnel changes degrading service quality or security posture.', 'third_party', 'identified', TRUE, 'General', ARRAY['vendor-personnel', 'continuity', 'quality']),
('rt000000-0000-0000-0006-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-013', 'Vendor Geopolitical Exposure', 'Risk of vendors operating in jurisdictions subject to sanctions, data access laws, or political instability.', 'third_party', 'identified', TRUE, 'General', ARRAY['geopolitical', 'jurisdiction', 'sanctions']),
('rt000000-0000-0000-0006-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-014', 'Vendor Incident Response Inadequacy', 'Risk of vendors having inadequate incident response capabilities, delaying breach notification and containment.', 'third_party', 'identified', TRUE, 'ISO 27036', ARRAY['vendor-ir', 'incident-response', 'notification']),
('rt000000-0000-0000-0006-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-015', 'Open Source Maintainer Risk', 'Risk of critical open-source dependencies being maintained by a single individual who may abandon the project.', 'third_party', 'identified', TRUE, 'General', ARRAY['open-source', 'maintainer', 'abandonment']),
('rt000000-0000-0000-0006-000000000016', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-016', 'Vendor SLA Non-Performance', 'Risk of vendors consistently failing to meet agreed service level objectives for availability, response time, or resolution.', 'third_party', 'identified', TRUE, 'ITIL', ARRAY['sla', 'performance', 'vendor-management']),
('rt000000-0000-0000-0006-000000000017', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-017', 'Vendor Data Deletion Failure', 'Risk of vendors failing to properly delete organizational data upon contract termination.', 'third_party', 'identified', TRUE, 'GDPR Art. 28', ARRAY['data-deletion', 'termination', 'retention']),
('rt000000-0000-0000-0006-000000000018', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-018', 'Cloud Provider IAM Misconfiguration', 'Risk of cloud provider IAM policies being misconfigured by vendor administrators, granting excessive permissions.', 'third_party', 'identified', TRUE, 'CIS Controls', ARRAY['iam', 'cloud', 'misconfiguration']),
('rt000000-0000-0000-0006-000000000019', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-019', 'Vendor Acquisition / Merger Risk', 'Risk of a vendor being acquired and the new owner changing terms, security posture, or service quality.', 'third_party', 'identified', TRUE, 'General', ARRAY['acquisition', 'merger', 'vendor-change']),
('rt000000-0000-0000-0006-000000000020', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-TP-020', 'Concentration Risk in Vendor Portfolio', 'Risk of excessive reliance on a small number of vendors creating systemic dependency.', 'third_party', 'identified', TRUE, 'General', ARRAY['concentration', 'diversification', 'systemic'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0007-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-001', 'Fraud / Financial Crime', 'Risk of internal or external fraud, including payment fraud, expense manipulation, or financial statement misrepresentation.', 'financial', 'identified', TRUE, 'General', ARRAY['fraud', 'financial-crime', 'misrepresentation']),
('rt000000-0000-0000-0007-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-002', 'Regulatory Fine', 'Risk of financial penalties from regulatory bodies for non-compliance with applicable laws and regulations.', 'financial', 'identified', TRUE, 'General', ARRAY['fine', 'penalty', 'regulatory']),
('rt000000-0000-0000-0007-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-003', 'Business Interruption Loss', 'Risk of financial loss due to extended business interruption from cyber incidents, outages, or disasters.', 'financial', 'identified', TRUE, 'ISO 22301', ARRAY['business-interruption', 'loss', 'recovery']),
('rt000000-0000-0000-0007-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-004', 'Cyber Insurance Coverage Gap', 'Risk of cyber insurance policies not covering the full scope of a cyber incident, leaving uninsured losses.', 'financial', 'identified', TRUE, 'General', ARRAY['insurance', 'coverage', 'gap']),
('rt000000-0000-0000-0007-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-005', 'Intellectual Property Theft', 'Risk of proprietary algorithms, source code, trade secrets, or business intelligence being stolen.', 'financial', 'identified', TRUE, 'General', ARRAY['ip-theft', 'trade-secret', 'proprietary']),
('rt000000-0000-0000-0007-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-006', 'Cryptocurrency / Digital Asset Loss', 'Risk of cryptocurrency or digital assets being stolen, lost, or rendered inaccessible due to key management failures.', 'financial', 'identified', TRUE, 'General', ARRAY['cryptocurrency', 'digital-asset', 'key-management']),
('rt000000-0000-0000-0007-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-007', 'Payment Processing Disruption', 'Risk of payment processing systems failing, preventing revenue collection and customer transactions.', 'financial', 'identified', TRUE, 'PCI DSS', ARRAY['payment', 'processing', 'revenue']),
('rt000000-0000-0000-0007-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-008', 'Data Breach Remediation Cost', 'Risk of significant financial costs from breach response including forensics, notification, credit monitoring, and legal fees.', 'financial', 'identified', TRUE, 'Ponemon Institute', ARRAY['breach-cost', 'remediation', 'forensics']),
('rt000000-0000-0000-0007-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-009', 'Revenue Loss from Service Downtime', 'Risk of direct revenue loss during service outages, proportional to downtime duration and traffic volume.', 'financial', 'identified', TRUE, 'General', ARRAY['revenue-loss', 'downtime', 'sla-credits']),
('rt000000-0000-0000-0007-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-010', 'Security Tool Overspend', 'Risk of overspending on security tools and technologies without proportional risk reduction.', 'financial', 'identified', TRUE, 'General', ARRAY['budget', 'overspend', 'roi']),
('rt000000-0000-0000-0007-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-011', 'Cloud Cost Overrun', 'Risk of cloud infrastructure costs exceeding budget due to misconfigured auto-scaling, orphaned resources, or data egress fees.', 'financial', 'identified', TRUE, 'General', ARRAY['cloud-cost', 'budget', 'overrun']),
('rt000000-0000-0000-0007-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-012', 'Customer Contract Loss Due to Security', 'Risk of losing existing customers or failing to win new business due to inadequate security posture or certifications.', 'financial', 'identified', TRUE, 'General', ARRAY['customer-loss', 'contract', 'trust']),
('rt000000-0000-0000-0007-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-013', 'Ransomware Payment Decision', 'Risk of being forced to make a ransomware payment decision with legal, financial, and ethical implications.', 'financial', 'identified', TRUE, 'OFAC', ARRAY['ransomware', 'payment', 'ethical']),
('rt000000-0000-0000-0007-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-014', 'Tax Compliance Risk from Data Handling', 'Risk of improper data handling leading to tax compliance issues across multiple jurisdictions.', 'financial', 'identified', TRUE, 'General', ARRAY['tax', 'compliance', 'jurisdiction']),
('rt000000-0000-0000-0007-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-FI-015', 'Vendor Price Increase Risk', 'Risk of critical vendor unexpectedly increasing prices significantly, straining budgets and forcing migration decisions.', 'financial', 'identified', TRUE, 'General', ARRAY['vendor-pricing', 'budget', 'migration'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0008-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-001', 'Class Action Lawsuit (Data Breach)', 'Risk of class action litigation following a data breach affecting a large number of individuals.', 'legal', 'identified', TRUE, 'General', ARRAY['lawsuit', 'class-action', 'data-breach']),
('rt000000-0000-0000-0008-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-002', 'Contract Breach Liability', 'Risk of failing to meet contractual security, privacy, or service level obligations to customers or partners.', 'legal', 'identified', TRUE, 'General', ARRAY['contract', 'breach', 'liability', 'sla']),
('rt000000-0000-0000-0008-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-003', 'Patent / IP Infringement', 'Risk of unintentionally infringing on third-party patents, trademarks, or copyrights.', 'legal', 'identified', TRUE, 'General', ARRAY['patent', 'copyright', 'infringement']),
('rt000000-0000-0000-0008-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-004', 'E-Discovery Non-Compliance', 'Risk of failing to preserve and produce electronic records in response to legal discovery requests.', 'legal', 'identified', TRUE, 'General', ARRAY['e-discovery', 'preservation', 'litigation-hold']),
('rt000000-0000-0000-0008-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-005', 'Open Source License Violation', 'Risk of violating open-source software licenses (GPL, AGPL, etc.) through improper use or distribution.', 'legal', 'identified', TRUE, 'General', ARRAY['open-source', 'license', 'gpl', 'compliance']),
('rt000000-0000-0000-0008-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-006', 'Whistleblower Retaliation Claim', 'Risk of legal action from employees claiming retaliation for reporting security or compliance concerns.', 'legal', 'identified', TRUE, 'General', ARRAY['whistleblower', 'retaliation', 'employment']),
('rt000000-0000-0000-0008-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-007', 'Negligence Liability (Security)', 'Risk of being found legally negligent for failing to implement reasonable security measures.', 'legal', 'identified', TRUE, 'General', ARRAY['negligence', 'reasonable-security', 'liability']),
('rt000000-0000-0000-0008-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-008', 'Regulatory Investigation', 'Risk of triggering a formal regulatory investigation due to suspected compliance violations or complaints.', 'legal', 'identified', TRUE, 'General', ARRAY['investigation', 'regulatory', 'complaint']),
('rt000000-0000-0000-0008-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-009', 'Cross-Jurisdiction Legal Conflict', 'Risk of conflicting legal requirements across jurisdictions creating compliance impossibilities.', 'legal', 'identified', TRUE, 'General', ARRAY['jurisdiction', 'conflict', 'cross-border']),
('rt000000-0000-0000-0008-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-010', 'Terms of Service / EULA Inadequacy', 'Risk of terms of service being insufficient to limit liability or enforce data handling requirements.', 'legal', 'identified', TRUE, 'General', ARRAY['tos', 'eula', 'liability-limitation']),
('rt000000-0000-0000-0008-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-011', 'Shareholder Derivative Action', 'Risk of shareholders suing the board for failure to adequately oversee cybersecurity risks.', 'legal', 'identified', TRUE, 'General', ARRAY['shareholder', 'derivative', 'governance']),
('rt000000-0000-0000-0008-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-012', 'Insurance Claim Denial', 'Risk of cyber insurance claims being denied due to policy exclusions, non-compliance with conditions, or late notification.', 'legal', 'identified', TRUE, 'General', ARRAY['insurance', 'claim-denial', 'exclusion']),
('rt000000-0000-0000-0008-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-013', 'Government Subpoena / Data Request', 'Risk of government agencies requesting access to customer or employee data through subpoenas or national security orders.', 'legal', 'identified', TRUE, 'General', ARRAY['subpoena', 'government', 'data-request']),
('rt000000-0000-0000-0008-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-014', 'AI Liability (Generated Content)', 'Risk of legal liability from AI-generated content that is defamatory, inaccurate, or infringes on rights.', 'legal', 'identified', TRUE, 'EU AI Act', ARRAY['ai', 'liability', 'generated-content']),
('rt000000-0000-0000-0008-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-LG-015', 'Employee Monitoring Legal Challenge', 'Risk of employee monitoring practices being challenged as violating privacy or employment laws.', 'legal', 'identified', TRUE, 'General', ARRAY['monitoring', 'employee-privacy', 'surveillance'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-0009-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-001', 'Public Data Breach Disclosure', 'Risk of a data breach becoming public, damaging brand reputation and customer trust.', 'reputational', 'identified', TRUE, 'General', ARRAY['breach-disclosure', 'reputation', 'media']),
('rt000000-0000-0000-0009-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-002', 'Customer Trust Erosion', 'Risk of security incidents, privacy violations, or poor handling of customer data eroding trust.', 'reputational', 'identified', TRUE, 'General', ARRAY['customer-trust', 'churn', 'confidence']),
('rt000000-0000-0000-0009-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-003', 'Social Media Security Incident', 'Risk of organizational social media accounts being compromised or used to spread misinformation.', 'reputational', 'identified', TRUE, 'General', ARRAY['social-media', 'compromise', 'account-takeover']),
('rt000000-0000-0000-0009-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-004', 'Negative Audit Finding Disclosure', 'Risk of qualified audit findings becoming known to customers, partners, or the public.', 'reputational', 'identified', TRUE, 'General', ARRAY['audit-finding', 'qualified', 'disclosure']),
('rt000000-0000-0000-0009-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-005', 'Employee Public Misconduct', 'Risk of employee misconduct in public forums or social media reflecting poorly on the organization.', 'reputational', 'identified', TRUE, 'General', ARRAY['employee-conduct', 'public', 'social-media']),
('rt000000-0000-0000-0009-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-006', 'Competitor Disinformation Campaign', 'Risk of competitors spreading false information about security practices or product quality.', 'reputational', 'identified', TRUE, 'General', ARRAY['disinformation', 'competitor', 'false-claims']),
('rt000000-0000-0000-0009-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-007', 'Poor Incident Communication', 'Risk of poorly managed public communication during a security incident worsening reputational damage.', 'reputational', 'identified', TRUE, 'NIST CSF', ARRAY['communication', 'incident', 'pr-crisis']),
('rt000000-0000-0000-0009-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-008', 'Partner/Vendor Reputation Spillover', 'Risk of a vendor or partner''s security incident or scandal negatively impacting organizational reputation.', 'reputational', 'identified', TRUE, 'General', ARRAY['vendor', 'spillover', 'association']),
('rt000000-0000-0000-0009-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-009', 'Bug Bounty / Responsible Disclosure Mishandling', 'Risk of poorly handling security researcher reports, leading to public disclosure, negative press, or researcher hostility.', 'reputational', 'identified', TRUE, 'General', ARRAY['bug-bounty', 'disclosure', 'researcher']),
('rt000000-0000-0000-0009-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-010', 'Glassdoor / Employer Brand Damage', 'Risk of negative employer reviews citing security culture, tool access, or management practices damaging recruitment.', 'reputational', 'identified', TRUE, 'General', ARRAY['employer-brand', 'glassdoor', 'recruitment']),
('rt000000-0000-0000-0009-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-011', 'Service Reliability Perception', 'Risk of repeated minor incidents eroding public perception of service reliability even if SLAs are met.', 'reputational', 'identified', TRUE, 'General', ARRAY['reliability', 'perception', 'incidents']),
('rt000000-0000-0000-0009-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-012', 'Ethical AI Controversy', 'Risk of AI systems producing biased, harmful, or controversial outputs that attract negative public attention.', 'reputational', 'identified', TRUE, 'General', ARRAY['ai', 'ethics', 'bias', 'controversy']),
('rt000000-0000-0000-0009-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-013', 'Open Source Community Backlash', 'Risk of license changes, contribution policies, or community management decisions causing developer backlash.', 'reputational', 'identified', TRUE, 'General', ARRAY['open-source', 'community', 'backlash']),
('rt000000-0000-0000-0009-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-014', 'Security Certification Marketing Misuse', 'Risk of incorrectly claiming or implying security certifications, leading to regulatory action or trust damage.', 'reputational', 'identified', TRUE, 'General', ARRAY['certification', 'marketing', 'misrepresentation']),
('rt000000-0000-0000-0009-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-RE-015', 'Status Page / Transparency Failure', 'Risk of failing to maintain accurate public status pages or transparency reports, eroding stakeholder confidence.', 'reputational', 'identified', TRUE, 'General', ARRAY['status-page', 'transparency', 'communication'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-000a-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-001', 'Key Person Dependency', 'Risk of critical knowledge, skills, or access being concentrated in a single individual with no backup.', 'human_resources', 'identified', TRUE, 'ISO 27005', ARRAY['key-person', 'bus-factor', 'knowledge']),
('rt000000-0000-0000-000a-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-002', 'Insufficient Security Training', 'Risk of employees lacking adequate security awareness training, increasing vulnerability to social engineering.', 'human_resources', 'identified', TRUE, 'CIS Controls', ARRAY['training', 'awareness', 'security-culture']),
('rt000000-0000-0000-000a-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-003', 'Insider Threat — Negligent', 'Risk of employees unintentionally causing security incidents through negligence, carelessness, or lack of awareness.', 'human_resources', 'identified', TRUE, 'NIST SP 800-30', ARRAY['insider-threat', 'negligence', 'human-error']),
('rt000000-0000-0000-000a-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-004', 'Inadequate Background Checks', 'Risk of hiring individuals with undisclosed criminal history or misrepresented qualifications.', 'human_resources', 'identified', TRUE, 'General', ARRAY['background-check', 'screening', 'hiring']),
('rt000000-0000-0000-000a-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-005', 'Improper Offboarding', 'Risk of departing employees retaining access to systems, data, or facilities after termination.', 'human_resources', 'identified', TRUE, 'CIS Controls', ARRAY['offboarding', 'access-revocation', 'termination']),
('rt000000-0000-0000-000a-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-006', 'Security Talent Shortage', 'Risk of being unable to recruit or retain qualified cybersecurity professionals due to market competition.', 'human_resources', 'identified', TRUE, 'General', ARRAY['talent', 'recruitment', 'retention']),
('rt000000-0000-0000-000a-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-007', 'Security Champion Program Gap', 'Risk of engineering teams lacking embedded security advocates, leading to security being an afterthought in development.', 'human_resources', 'identified', TRUE, 'General', ARRAY['security-champion', 'devsecops', 'culture']),
('rt000000-0000-0000-000a-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-008', 'Burnout and Alert Fatigue', 'Risk of security team burnout from alert fatigue, on-call pressure, and incident response demands.', 'human_resources', 'identified', TRUE, 'General', ARRAY['burnout', 'alert-fatigue', 'on-call']),
('rt000000-0000-0000-000a-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-009', 'Contractor Security Posture', 'Risk of contractors and temporary staff having weaker security practices or less investment in organizational security culture.', 'human_resources', 'identified', TRUE, 'General', ARRAY['contractor', 'temporary-staff', 'security-culture']),
('rt000000-0000-0000-000a-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-010', 'Remote Work Security Gap', 'Risk of remote workers using insecure home networks, personal devices, or public Wi-Fi for work activities.', 'human_resources', 'identified', TRUE, 'CIS Controls', ARRAY['remote-work', 'home-network', 'byod']),
('rt000000-0000-0000-000a-000000000011', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-011', 'Knowledge Transfer Failure', 'Risk of critical institutional knowledge being lost when employees depart without adequate documentation or handover.', 'human_resources', 'identified', TRUE, 'General', ARRAY['knowledge-transfer', 'documentation', 'handover']),
('rt000000-0000-0000-000a-000000000012', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-012', 'Disgruntled Employee Risk', 'Risk of employees who are unhappy, under performance review, or facing termination taking retaliatory actions against systems or data.', 'human_resources', 'identified', TRUE, 'NIST SP 800-30', ARRAY['disgruntled', 'retaliation', 'insider-threat']),
('rt000000-0000-0000-000a-000000000013', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-013', 'Security Team Understaffing', 'Risk of security team being understaffed relative to the organization''s risk profile, leading to coverage gaps.', 'human_resources', 'identified', TRUE, 'General', ARRAY['understaffing', 'coverage', 'workload']),
('rt000000-0000-0000-000a-000000000014', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-014', 'Skills Gap in Emerging Technologies', 'Risk of staff lacking skills in emerging areas (cloud security, AI security, container security, zero trust).', 'human_resources', 'identified', TRUE, 'General', ARRAY['skills-gap', 'emerging-tech', 'training']),
('rt000000-0000-0000-000a-000000000015', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-HR-015', 'Cultural Resistance to Security', 'Risk of organizational culture viewing security as an impediment rather than enabler, leading to shadow IT and workarounds.', 'human_resources', 'identified', TRUE, 'General', ARRAY['culture', 'resistance', 'shadow-it'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-000b-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-001', 'AI / Emerging Technology Risk', 'Risk of AI systems introducing uncontrolled decision-making, bias, or security vulnerabilities into business processes.', 'strategic', 'identified', TRUE, 'ISO 42001', ARRAY['ai', 'emerging-tech', 'automation']),
('rt000000-0000-0000-000b-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-002', 'Market / Competitive Disruption', 'Risk of competitors or market shifts rendering current products, services, or security investments obsolete.', 'strategic', 'identified', TRUE, 'General', ARRAY['market', 'competition', 'disruption']),
('rt000000-0000-0000-000b-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-003', 'M&A Integration Risk', 'Risk of security and compliance gaps during mergers, acquisitions, or divestitures.', 'strategic', 'identified', TRUE, 'General', ARRAY['m-and-a', 'integration', 'due-diligence']),
('rt000000-0000-0000-000b-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-004', 'Geopolitical / Sanctions Risk', 'Risk of operating in or with entities from regions subject to sanctions or geopolitical instability.', 'strategic', 'identified', TRUE, 'General', ARRAY['geopolitical', 'sanctions', 'export-control']),
('rt000000-0000-0000-000b-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-005', 'Digital Transformation Failure', 'Risk of digital transformation initiatives failing to deliver expected security, efficiency, or compliance improvements.', 'strategic', 'identified', TRUE, 'General', ARRAY['digital-transformation', 'modernization', 'cloud-migration']),
('rt000000-0000-0000-000b-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-006', 'Security Strategy Misalignment', 'Risk of cybersecurity strategy not aligning with business objectives, leading to wasted investment or unaddressed risks.', 'strategic', 'identified', TRUE, 'NIST CSF', ARRAY['strategy', 'alignment', 'business-objectives']),
('rt000000-0000-0000-000b-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-007', 'Board / Executive Cyber Literacy Gap', 'Risk of board members and executives lacking sufficient cybersecurity understanding to make informed risk decisions.', 'strategic', 'identified', TRUE, 'SEC Cyber Rules', ARRAY['board', 'governance', 'literacy']),
('rt000000-0000-0000-000b-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-008', 'Zero Trust Adoption Failure', 'Risk of zero trust architecture adoption failing due to complexity, cost, or organizational resistance.', 'strategic', 'identified', TRUE, 'NIST SP 800-207', ARRAY['zero-trust', 'architecture', 'adoption']),
('rt000000-0000-0000-000b-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-009', 'Cloud Migration Security Gap', 'Risk of security controls being inadequate or misconfigured during cloud migration projects.', 'strategic', 'identified', TRUE, 'CSA CCM', ARRAY['cloud-migration', 'security-gap', 'hybrid']),
('rt000000-0000-0000-000b-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-ST-010', 'Innovation vs Security Balance', 'Risk of overly restrictive security controls stifling innovation, or lax controls enabling rapid but insecure development.', 'strategic', 'identified', TRUE, 'General', ARRAY['innovation', 'balance', 'agility'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-000c-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-001', 'Unauthorized Physical Access', 'Risk of unauthorized individuals gaining physical access to data centers, server rooms, or restricted areas.', 'physical', 'identified', TRUE, 'ISO 27001 A.11', ARRAY['physical-access', 'data-center', 'restricted-area']),
('rt000000-0000-0000-000c-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-002', 'Natural Disaster Impact', 'Risk of earthquakes, floods, fires, or severe weather damaging physical infrastructure and disrupting operations.', 'physical', 'identified', TRUE, 'ISO 22301', ARRAY['natural-disaster', 'flood', 'earthquake', 'fire']),
('rt000000-0000-0000-000c-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-003', 'Power / Utility Failure', 'Risk of extended power outages or utility failures affecting data center or office operations.', 'physical', 'identified', TRUE, 'ISO 27005', ARRAY['power', 'utility', 'ups', 'generator']),
('rt000000-0000-0000-000c-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-004', 'Hardware Theft or Tampering', 'Risk of physical hardware (servers, laptops, storage media) being stolen or tampered with.', 'physical', 'identified', TRUE, 'ISO 27001 A.11', ARRAY['theft', 'tampering', 'hardware']),
('rt000000-0000-0000-000c-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-005', 'Environmental Control Failure', 'Risk of HVAC, fire suppression, or environmental monitoring systems failing in data center environments.', 'physical', 'identified', TRUE, 'ISO 27001 A.11', ARRAY['hvac', 'fire-suppression', 'environmental']),
('rt000000-0000-0000-000c-000000000006', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-006', 'Visitor Access Control Failure', 'Risk of visitor management processes failing, allowing unauthorized individuals into secure areas without proper escort.', 'physical', 'identified', TRUE, 'ISO 27001 A.11', ARRAY['visitor', 'escort', 'access-control']),
('rt000000-0000-0000-000c-000000000007', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-007', 'CCTV / Surveillance Failure', 'Risk of security camera systems failing or being tampered with, eliminating visual evidence of physical security events.', 'physical', 'identified', TRUE, 'General', ARRAY['cctv', 'surveillance', 'monitoring']),
('rt000000-0000-0000-000c-000000000008', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-008', 'Cable / Network Infrastructure Damage', 'Risk of physical damage to network cabling or infrastructure causing connectivity loss or security bypass.', 'physical', 'identified', TRUE, 'General', ARRAY['cabling', 'infrastructure', 'damage']),
('rt000000-0000-0000-000c-000000000009', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-009', 'Electromagnetic Interference', 'Risk of electromagnetic interference disrupting sensitive electronic equipment or data integrity.', 'physical', 'identified', TRUE, 'ISO 27005', ARRAY['emi', 'interference', 'electronic']),
('rt000000-0000-0000-000c-000000000010', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-PH-010', 'Removable Media Loss', 'Risk of USB drives, external drives, or backup tapes containing sensitive data being lost or stolen.', 'physical', 'identified', TRUE, 'CIS Controls', ARRAY['removable-media', 'usb', 'tape', 'loss'])
INSERT INTO risks (id, org_id, identifier, title, description, category, status, is_template, template_source, tags) VALUES
('rt000000-0000-0000-000d-000000000001', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-EN-001', 'E-Waste Disposal Non-Compliance', 'Risk of improper disposal of electronic equipment containing sensitive data, violating environmental and data protection regulations.', 'environmental', 'identified', TRUE, 'General', ARRAY['e-waste', 'disposal', 'data-destruction']),
('rt000000-0000-0000-000d-000000000002', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-EN-002', 'Carbon Footprint Reporting Pressure', 'Risk of increasing regulatory and stakeholder pressure to report and reduce digital infrastructure carbon footprint.', 'environmental', 'identified', TRUE, 'General', ARRAY['carbon', 'sustainability', 'reporting']),
('rt000000-0000-0000-000d-000000000003', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-EN-003', 'Climate Change Physical Risk', 'Risk of climate change increasing frequency of extreme weather events affecting data center operations and business continuity.', 'environmental', 'identified', TRUE, 'TCFD', ARRAY['climate', 'extreme-weather', 'continuity']),
('rt000000-0000-0000-000d-000000000004', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-EN-004', 'Energy Cost Volatility', 'Risk of volatile energy prices significantly impacting data center operational costs.', 'environmental', 'identified', TRUE, 'General', ARRAY['energy', 'cost', 'volatility']),
('rt000000-0000-0000-000d-000000000005', 'a0000000-0000-0000-0000-000000000001', 'TPL-RISK-EN-005', 'Water Usage Restrictions', 'Risk of water usage restrictions affecting data center cooling systems in drought-prone regions.', 'environmental', 'identified', TRUE, 'General', ARRAY['water', 'cooling', 'drought'])
